sqlMap.select:
SELECT COUNT(ID_SCHEDRIC) FROM W_SCHEDRIC, W_RICERCHE WHERE ID_SCHEDRIC = #idSchedRic# AND W_SCHEDRIC.ID_RICERCA = W_RICERCHE.ID_RICERCA AND (( ID_PROSPETTO IS NULL AND NOT EXISTS( SELECT ID_RICERCA FROM W_RICPARAM WHERE W_RICPARAM.ID_RICERCA = W_RICERCHE.ID_RICERCA) ) OR ( ID_PROSPETTO IS NOT NULL AND NOT EXISTS( SELECT ID_MODELLO FROM W_MODPARAM WHERE W_MODPARAM.ID_MODELLO = W_RICERCHE.ID_PROSPETTO
W_MODPARAM.TIPO <> #tipoParametro#)
))

		select id_schedric
		from w_schedric
		where attivo = 1
		and codapp = #codiceApplicazione#
		and (
					(     data_prox_esec = #dataProxEsec#
          	and (ora_avvio*60 + minuto_avvio) = #oraInMinuti#
          ) or (
               	 (    data_prox_esec < #dataProxEsec#
	          		  or (data_prox_esec = #dataProxEsec# and (ora_avvio*60 + minuto_avvio) < #oraInMinuti#)
	               )
               )
         )
